// ---------------------------------

type IntStack struct {
	stack []int
}

func (stack *IntStack) push(s int) {
	stack.stack = append(stack.stack, s)
}

func (stack *IntStack) pop() int {
	if len(stack.stack) == 0 {
		panic("stack is empty")
	}
	node := stack.stack[len(stack.stack)-1]
	stack.stack = stack.stack[:len(stack.stack)-1]
	return node
}

func (stack *IntStack) reset() {
	stack.stack = stack.stack[:0]
}

func (stack *IntStack) len() int {
	return len(stack.stack)
}

func (stack *IntStack) peek() int {
	return stack.stack[len(stack.stack)-1]
}

// ---------------------------------

func longestValidParentheses(s string) int {
	//return approachStack(s)
	return approachDP(s)
}

func approachStack(s string) int {
	stack := &IntStack{}
	result := 0

	stack.push(-1)

	for i, v := range s {

		if v == '(' {
			stack.push(i)
		}

		if v == ')' {
			stack.pop()

			if stack.len() == 0 {
				stack.push(i)
			} else {
				length := i - stack.peek()
				result = max(result, length)
			}
		}
	}

	return result
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

func approachDP(s string) int {
	dp := make([]int, len(s))
	maxLen := 0

	for i := 1; i < len(s); i++ {

		previous := s[i-1]
		current := s[i]

		if current == ')' {

			if previous == '(' {

				if i >= 2 {
					dp[i] = dp[i-2] + 2
					maxLen = max(dp[i], maxLen)
				} else {
					dp[i] = 2
				}

			} else if i-dp[i-1]-1 >= 0 && s[i-dp[i-1]-1] == '(' {
				// `i - dp[i-1] - 1 >= 0` means there is a char didn't match up in previous loop.
				// `s[i - dp[i-1] - 1] == '('` means the char didn't match up in previous loop can be matched with s[i].
				dp[i] = dp[i-1] + 2
				if i-dp[i-1]-2 >= 0 {
					// `i - dp[i-1] - 2` means there still have valid matched pair before that '(' char.
					dp[i] += dp[i-dp[i-1]-2]
				}
			}

			maxLen = max(maxLen, dp[i])
		}
	}

	return maxLen
}

